import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './login.css';

const Login = ({ setLoginUser }) => {
  const history = useNavigate();

  const [user, setUser] = useState({
    email: '',
    password: '',
    isDoctor: false,
  });

  const login = async () => {
    try {
      let loginUrl = 'http://localhost:9002/login';
      if (user.isDoctor) {
        loginUrl = 'http://localhost:9002/login/doctor'; // Use the doctor login route if user is a doctor
      }
      const response = await axios.post(loginUrl, { email: user.email, password: user.password });
      const { message, user: loggedInUser, token } = response.data;
  
      if (message === "Login Successful") {
        localStorage.setItem('token', token);
        alert(message);
        setLoginUser(loggedInUser);
        history('/');
      } else {
        alert(message);
      }
    } catch (error) {
      console.error(user.isDoctor ? 'Doctor login error:' : 'Login error:', error);
    }
  };
  const loginDoctor = async () => {
    try {
      // Add logic specific to doctor login
      // For example, you might want to set isDoctor to true before calling the login function
      setUser({ ...user, isDoctor: true });
      
      // Call the login function
      const response = await axios.post('http://localhost:9002/login/doctor', { email: user.email, password: user.password });
      const { message, user: loggedInUser, token } = response.data;
  
      if (message === "Login Successful") {
        localStorage.setItem('token', token);
        alert(message);
        setLoginUser(loggedInUser);
        history('/');
      } else {
        alert(message);
      }
    } catch (error) {
      console.error('Doctor login error:', error);
    }
  };
  

  return (
    <div className="login">
      <h1>Login</h1>
      <input type="text" name="email" value={user.email} onChange={(e) => setUser({ ...user, email: e.target.value })} placeholder="Enter your Email" />
      <input type="password" name="password" value={user.password} onChange={(e) => setUser({ ...user, password: e.target.value })} placeholder="Enter your Password" />
    
      <label>
        Are you a doctor?
        <input
          type="checkbox"
          name="isDoctor"
          checked={user.isDoctor}
          onChange={(e) => setUser({ ...user, isDoctor: e.target.checked })}
        />
      </label>

      <div className="button" onClick={user.isDoctor ? loginDoctor : login}>Login</div>
      <div>or</div>
      <div className="button" onClick={() => history('/register')}>Register</div>
    </div>
  );
};

export default Login;
