import React, { useState, useEffect } from 'react';
import { CardElement, useStripe, useElements } from '@stripe/react-stripe-js';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const CheckoutForm = ({ clientSecret }) => {
  const [error, setError] = useState(null);
  const stripe = useStripe();
  const elements = useElements();
  const navigate = useNavigate();

  const handleSubmit = async (event) => {
    event.preventDefault();

    if (!stripe || !elements) {
      setError('Failed to load Stripe, please refresh the page.');
      return;
    }

    try {
      const result = await stripe.confirmCardPayment(clientSecret, {
        payment_method: {
          card: elements.getElement(CardElement),
          billing_details: {
            name: 'Tripti Bhattacharyya',
          },
        },
      });

      if (result.error) {
        setError(result.error.message);
      } else {
        if (result.paymentIntent.status === 'succeeded') {
          navigate('/payment/success');
        }
      }
    } catch (error) {
      setError('An error occurred while processing the payment.');
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <CardElement />
      <button type="submit" disabled={!stripe}>
        Pay Now
      </button>
      {error && <div>{error}</div>}
    </form>
  );
};

const Payment = ({ amount }) => {
  const [clientSecret, setClientSecret] = useState(null);

  useEffect(() => {
    const fetchClientSecret = async () => {
      try {
        const response = await axios.post('http://localhost:9002/payment', { amount: amount });
        setClientSecret(response.data.clientSecret);
      } catch (error) {
        console.error('Error fetching client secret:', error);
        setError('An error occurred while fetching the client secret. Please try again later.');
      }
    };

    fetchClientSecret();
  }, [amount]);

  return (
    <div>
      <h2>Payment</h2>
      {clientSecret && <CheckoutForm clientSecret={clientSecret} />}
    </div>
  );
};

export default Payment;
