// App.js
// App.js
import React, { useState, useEffect } from 'react';
import { Routes, Route, Navigate, BrowserRouter as Router } from 'react-router-dom';
import Nav from './Nav/Nav';
import Homepage from './components/homepage/homepage';
import Login from './components/login/ogin';
import Register from './components/register/Register';
import Hospitals from './components/hospitals/Hospitals';
import Doctors from './components/doctors/Doctors';
import Contacts from './components/contacts/Contacts';
import axios from 'axios';

function App() {
  const [user, setLoginUser] = useState({});
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const checkUserAuthentication = async () => {
      try {
        // Make an API call to check the user's authentication status
        const response = await axios.get("http://localhost:9002/check-auth");

        // Assuming the server responds with user information if authenticated
        const authenticatedUser = response.data.user;

        if (authenticatedUser) {
          // If the user is authenticated, set the user state
          setLoginUser(authenticatedUser);
        }
      } catch (error) {
        // Handle errors or redirect to the login page
        console.error("Error checking authentication:", error);

        // Assuming you want to redirect to the login page on authentication failure
        // history("/login");
      } finally {
        // Set loading to false regardless of the result
        setLoading(false);
      }
    };

    checkUserAuthentication();
  }, []);

  return (
    <div className="App">
      <Router>
        <Nav />
        <Routes>
          <Route
            path="/"
            element={
              loading ? (
                // Render a loading spinner or message while checking authentication
                <div>Loading...</div>
              ) : user && user._id ? (
                <Homepage setLoginUser={setLoginUser} />
              ) : (
                <Login setLoginUser={setLoginUser} />
              )
            }
          />
          <Route path="/login" element={<Login setLoginUser={setLoginUser} />} />
          <Route path="/register" element={<Register />} />
          {user && user._id ? (
            <Route path="/hospital" element={<Hospitals />} />
          ) : (
            <Route path="/hospital" element={<Navigate to="/login" />} />
          )}
          <Route path="/doctors" element={<Doctors />} />
          <Route path="/contacts" element={<Contacts />} />
        </Routes>
      </Router>
    </div>
  );
}

export default App;
