import React, { useState } from 'react';
import { Elements } from '@stripe/react-stripe-js';
import { loadStripe } from '@stripe/stripe-js';
import CheckoutForm from './CheckoutForm';
import axios from 'axios';

const stripePromise = loadStripe('pk_test_51OsfLSSDK53yDPuaMdZw2Vy4TyJGB8Nug8Yc4nvATKi7iOqIvLRli0SIdmkdsQBERIwMO1Ekb6GdW5TYO4pjz72W00Luof9MUU');

const Payment = () => {
  const [amount, setAmount] = useState(0); // Initialize amount state
  const [clientSecret, setClientSecret] = useState('');

  const handleAmountChange = (e) => {
    setAmount(e.target.value); // Update amount state when input value changes
  };

  const handlePayment = async () => {
    try {
      const response = await axios.post('/payment', { amount });
      setClientSecret(response.data.clientSecret);
    } catch (error) {
      console.error('Error fetching client secret:', error);
    }
  };

    return (
    <div>
      <h2>Payment</h2>
      <div>
        <label>
          Enter Amount:
          <input type="number" value={amount} onChange={handleAmountChange} />
        </label>
      </div>
      {amount > 0 && ( // Render CheckoutForm only if amount is greater than 0
        <Elements stripe={stripePromise}>
          <CheckoutForm clientSecret={clientSecret} onSubmit={handleSubmitPayment} />
        </Elements>
      )}
      <button onClick={handlePayment}>Pay Now</button>
    </div>
  );
};

export default Payment;
