// checkoutForm.js

import React, { useState } from 'react';
import axios from 'axios';
import PayNow from './payNow';

const CheckoutForm = () => {
  const [amount, setAmount] = useState(0);

  const handleAmountChange = (e) => {
    const value = parseFloat(e.target.value);
    //console.log(value);
    if (!isNaN(value) && value >= 0) {
      setAmount(value);
    }
  };
  
  

  const handleSubmit = async (event) => {
    event.preventDefault();
  
    // Ensure that a valid amount is entered
    if (!amount || isNaN(amount) || parseFloat(amount) <= 0) {
      alert('Please enter a valid amount greater than zero.');
      return;
    }
  
    try {
      // Make a request to your backend endpoint to create a payment intent
      const response = await axios.post('http://localhost:9002/payment', { amount });
      
      // Extract client secret from the response
      const clientSecret = response.data.clientSecret;
  
      // Pass the client secret to the PayNow component for payment processing
      setClientSecret(clientSecret); // Assuming you have a state to store client secret in CheckoutForm
    } catch (error) {
      console.error('Error creating payment intent:', error);
      alert('An error occurred while processing the payment. Please try again later.');
    }
  };
  

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <label>
          Enter Amount:
          <input type="number" value={amount} onChange={handleAmountChange} />
        </label>
      </form>
      
      {amount > 0 && (
        <PayNow  amount={amount} />
      )}
    </div>
      
  );
};

export default CheckoutForm;
