// NewChat.js
import React, { useState } from 'react';
import axios from 'axios';

const NewChat = () => {
    const [recipientId, setRecipientId] = useState('');
    const [messageText, setMessageText] = useState('');

    const sendMessage = () => {
        // Send a message to the recipient
        axios.post('http://localhost:9002/messages', { recipientId, text: messageText })
             .then(response => {
                 console.log('Message sent successfully');
                 // Reset form fields after sending message
                 setRecipientId('');
                 setMessageText('');
             })
             .catch(error => {
                 console.error('Error sending message:', error);
             });
    };

    return (
        <div>
            <h2>New Chat</h2>
            <input type="text" value={recipientId} onChange={e => setRecipientId(e.target.value)} placeholder="Recipient ID" />
            <textarea value={messageText} onChange={e => setMessageText(e.target.value)} placeholder="Type your message..." />
            <button onClick={sendMessage}>Send Message</button>
        </div>
    );
};

export default NewChat;
