import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Payment from '../Payment/payNow'; // Import the Payment component

const Appointments = ({ user }) => {
  const [appointments, setAppointments] = useState([]);
  const [paymentAmount, setPaymentAmount] = useState(0); // State to store payment amount

  useEffect(() => {
    const fetchAppointments = async () => {
      try {
        const token = localStorage.getItem('token');
        const response = await axios.get(`http://localhost:9002/appointments/${user._id}`, {
          headers: {
            Authorization: `Bearer ${token}`
          }
        });
        const filteredAppointments = response.data.filter(appointment => appointment.status !== 'Cancelled');
        setAppointments(filteredAppointments);
      } catch (error) {
        console.error('Error fetching appointments:', error);
      }
    };

    fetchAppointments();
  }, [user._id]);

const handlePaymentSuccess = async (appointmentId) => {
  try {
    const token = localStorage.getItem('token');
    // Call your backend endpoint to update the appointment status to 'Paid'
    await axios.put(`http://localhost:9002/appointments/${appointmentId}/paid`, null, {
      headers: {
        Authorization: `Bearer ${token}`
      }
    });
    console.log('Payment successful for appointment:', appointmentId);
    // Optionally, you can update the local state or fetch updated appointments from the server
  } catch (error) {
    console.error('Error updating appointment status:', error);
  }
};


  return (
    <div>
      <h2>Your Appointments</h2>
      <ul>
        {appointments.map(appointment => (
          <li key={appointment._id}>
            Doctor: {appointment.doctorId.name}, Date: {appointment.date}, Time: {appointment.timeSlot}, Status: {appointment.status}
            {appointment.status === 'Approved' && (
              <div>
                <input type="number" value={paymentAmount} onChange={(e) => setPaymentAmount(parseFloat(e.target.value))} />
                <Payment appointmentId={appointment._id} amount={paymentAmount} onPayment={() => handlePaymentSuccess(appointment._id)} />
              </div>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Appointments;
