import React, { useState, useEffect, useRef } from 'react';
import { useParams } from 'react-router-dom';
import io from 'socket.io-client';
import './UserChat.css'; // Import CSS file

const UserChat = (user) => {
    console.log(user.user);
    const { userId, doctorId } = useParams();
    const [messages, setMessages] = useState([]);
    const [newMessage, setNewMessage] = useState('');
    const [file, setFile] = useState(null);
    const socket = useRef(null); // Store socket instance in a ref

    const messageInputRef = useRef(null);

    useEffect(() => {
        // Initialize socket connection only once
        socket.current = io('http://localhost:9002', {
            query: {
                userId: user.user,
            }
        });

        // Retrieve initial messages
        socket.current.emit('getMessages', { userId, doctorId });

        // Listen for new messages
        socket.current.on('messages', (initialMessages) => {
            setMessages(initialMessages);
        });

        // Log when setting up the event listener for incoming messages
        console.log("Setting up event listener for incoming messages");

        // Listen for incoming messages
        socket.current.on('message', (message) => {
            console.log("Received message:", message);
            setMessages(prevMessages => [...prevMessages, message]); // Append the new message to the list
        });

        // Listen for incoming file messages
        socket.current.on('fileSent', (fileMessage) => {
            console.log("Received file message:", fileMessage);
            setMessages(prevMessages => [...prevMessages, fileMessage]); // Append the new file message to the list
        });

        // Clean up event listeners
        return () => {
            if (socket.current) {
                socket.current.off('messages');
                socket.current.off('message');
                socket.current.off('fileSent');
            }
        };
    }, [doctorId, userId, user.user]);
// Inside handleMessageSend function
const handleMessageSend = () => {
    if (newMessage.trim() === '' && !file) return;

    if (file) {
        const reader = new FileReader();
        reader.onload = () => {
            const fileData = reader.result;

            const message = {
                userId: userId,
                doctorId: doctorId,
                content: fileData,
                fileName: file.name,
                isFile: true, // Set isFile to true for file messages
            };

            socket.current.emit('sendMessage', message);
        };
        reader.readAsDataURL(file);
    } else {
        // If it's a text message, directly emit it with isFile set to false
        socket.current.emit('sendMessage', { userId, doctorId, content: newMessage, isFile: false });
    }

    setNewMessage('');
    setFile(null);
    messageInputRef.current.focus();
};


    const handleFileChange = (e) => {
        const selectedFile = e.target.files[0];
        setFile(selectedFile);
    };

    const renderFile = (fileMessage) => {
        // Decode the Base64 string to display the file content
        const decodedFile = atob(fileMessage.content.split(',')[1]);
    
        // Determine the appropriate content type
        const contentType = fileMessage.content.split(',')[0].split(':')[1].split(';')[0];
    
        // Display different file types accordingly
        switch (contentType) {
            case 'image/jpg':
            case 'image/jpeg':
            case 'image/png':
                // For images, display them using the decoded binary data
                return <img src={`data:${contentType};base64,${decodedFile}`} alt={fileMessage.fileName} />;
            case 'application/pdf':
                // For PDFs, display them using an embed tag
                return <embed src={`data:${contentType};base64,${decodedFile}`} type={contentType} />;
            default:
                // For other file types, provide a download link
                return (
                    <div>
                        <p>File type: {contentType}</p>
                        <a href={`data:${contentType};base64,${fileMessage.content}`} download={fileMessage.fileName}>Download {fileMessage.fileName}</a>
                    </div>
                );
        }
    };
    
    

    return (
        <div className="user-chat-container">
            <h2>Chat</h2>
          
            <div className="chat-messages">
                {messages.map((message) => (
                    <div key={message._id} className={`message ${message.senderId === user.user ? 'left' : 'right'}`}>
                        {message.isFile ? ( // Check if the message is a file
                            // Render the file content
                            renderFile(message)
                        ) : (
                            // Display text message
                            <>
                                <p className="message-content">{message.content}</p>
                                <span className="message-timestamp">{message.createdAt}</span>
                            </>
                        )}
                    </div>
                ))}
            </div>

            <div className="input-container">
                <input
                    type="text"
                    value={newMessage}
                    onChange={(e) => setNewMessage(e.target.value)}
                    ref={messageInputRef}
                    className="message-input"
                />
                <input type="file" onChange={handleFileChange} />
                <button onClick={handleMessageSend} className="send-button">Send</button>
            </div>
        </div>
    );
};

export default UserChat;
