import React, { useState, useEffect } from 'react';
import axios from 'axios';

const DoctorProfile = ({ match }) => {
  const [doctor, setDoctor] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchDoctorData = async () => {
      try {
        const response = await axios.get(`http://localhost:9002/doctors/${match.params.id}`); // Assuming your backend API endpoint for fetching a single doctor's data is '/doctors/:id'
        setDoctor(response.data);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching doctor data:', error);
        setLoading(false);
      }
    };

    fetchDoctorData();
  }, [match.params.id]);

  if (loading) {
    return <p>Loading...</p>;
  }

  if (!doctor) {
    return <p>Doctor not found</p>;
  }

  return (
    <div>
      <h2>Doctor Profile</h2>
      <div>
        <h3>{doctor.name}</h3>
        <p>Degree: {doctor.degree}</p>
        <p>Specialty: {doctor.specialty}</p>
        <p>Experience: {doctor.experience} years</p>
      
      </div>
    </div>
  );
};

export default DoctorProfile;
