// Chat.js
import React, { useState, useEffect } from 'react';

const Chat = ({ user, sendMessage }) => {
  const [message, setMessage] = useState('');
  const [messages, setMessages] = useState([]);

  useEffect(() => {
    // Event listener for receiving messages
    if (socket) {
      socket.on('chat message', (msg) => {
        setMessages([...messages, msg]);
      });
    }

    return () => {
      if (socket) {
        socket.off('chat message');
      }
    };
  }, [messages]); // Re-register event listener when messages state changes

  const handleSubmit = (e) => {
    e.preventDefault();
    if (message.trim() !== '') {
      sendMessage(message); // Send message via WebSocket
      setMessages([...messages, { text: message, sender: user._id }]);
      setMessage('');
    }
  };

  return (
    <div>
      <h2>Doctor Chat</h2>
      <div className="chat-container">
        {messages.map((msg, index) => (
          <div key={index} className={`message ${msg.sender === user._id ? 'sent' : 'received'}`}>
            {msg.text}
          </div>
        ))}
      </div>
      <form onSubmit={handleSubmit}>
        <input type="text" value={message} onChange={(e) => setMessage(e.target.value)} />
        <button type="submit">Send</button>
      </form>
    </div>
  );
};

export default Chat;
