import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

const DocRegister = () => {
  const navigate = useNavigate();
  const [doctor, setDoctor] = useState({
    name: "",
    email: "",
    password: "",
    specialization: "",
    degree: "",
    photo: null, // New state for storing the uploaded photo
    timeslots: {
      monday: { start: "", end: "" },
    },
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setDoctor((prevDoctor) => ({
      ...prevDoctor,
      [name]: value,
    }));
  };

  const handlePhotoChange = (e) => {
    const file = e.target.files[0];
    setDoctor((prevDoctor) => ({
      ...prevDoctor,
      photo: file,
    }));
  };

  const registerDoctor = async () => {
    const formData = new FormData();
    formData.append("name", doctor.name);
    formData.append("email", doctor.email);
    formData.append("password", doctor.password);
    formData.append("specialization", doctor.specialization);
    formData.append("degree", doctor.degree);
    formData.append("photo", doctor.photo); // Append photo to FormData

    try {
      const response = await axios.post("http://localhost:9002/register/doctor", formData, {
        headers: {
          "Content-Type": "multipart/form-data",
        },
      });
      alert(response.data.message);
      navigate("/login");
    } catch (error) {
      console.error("Doctor registration error:", error);
      alert("An error occurred during registration");
    }
  };
  
  

  const validateTimeslots = (timeslots) => {
    for (const day in timeslots) {
      if (!timeslots[day].start || !timeslots[day].end) {
        return false;
      }
    }
    return true;
  };

  return (
    <div className="doc-register">
      <h1>Doctor Registration</h1>
      <input
        type="text"
        name="name"
        value={doctor.name}
        placeholder="Your Name"
        onChange={handleChange}
      />
      <input
        type="text"
        name="email"
        value={doctor.email}
        placeholder="Your Email"
        onChange={handleChange}
      />
      <input
        type="password"
        name="password"
        value={doctor.password}
        placeholder="Your Password"
        onChange={handleChange}
      />
      <input
        type="text"
        name="specialization"
        value={doctor.specialization}
        placeholder="Your Specialization"
        onChange={handleChange}
      />
      <input
        type="text"
        name="degree"
        value={doctor.degree}
        placeholder="Your Degree"
        onChange={handleChange}
      />
      <div>
        <label>Monday:</label>
        <select value={doctor.timeslots.monday.start} onChange={(e) => handleTimeslotChange(e, "monday", "start")}>
          <option value="">Select Start Time</option>
          <option value="9:00 AM">9:00 AM</option>
          <option value="10:00 AM">10:00 AM</option>
          {/* Add more options as needed */}
        </select>
        <select value={doctor.timeslots.monday.end} onChange={(e) => handleTimeslotChange(e, "monday", "end")}>
          <option value="">Select End Time</option>
          <option value="12:00 PM">12:00 PM</option>
          <option value="1:00 PM">1:00 PM</option>
          {/* Add more options as needed */}
        </select>
      </div>
      {/* Repeat similar input structure for other days */}
      <div className="button" onClick={registerDoctor}>
        Register as Doctor
      </div>
    </div>
  );
};

export default DocRegister;
