import React, { useState, useEffect } from 'react';
import axios from 'axios';
import NewChat from './NewChat'; // Import NewChat component

const ListConversations = () => {
    const [conversations, setConversations] = useState([]);

    useEffect(() => {
        const token = localStorage.getItem('token'); // Retrieve token from local storage

        // Fetch list of conversations from backend API with token included in headers
        axios.get('http://localhost:9002/conversations', {
            headers: {
                Authorization: `Bearer ${token}` // Include token in authorization header
            }
        })
        .then(response => {
            setConversations(response.data);
        })
        .catch(error => {
            console.error('Error fetching conversations:', error);
        });
    }, []);

    return (
        <div>
            <h2>Conversations</h2>
            <ul>
                {conversations.map(conversation => (
                    <li key={conversation._id}>{conversation.participants.name}</li>
                ))}
            </ul>

            {/* Render NewChat component */}
           
        </div>
    );
};

export default ListConversations;
