// App.js
// Import necessary libraries and components
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Routes, Route, Navigate, BrowserRouter as Router } from 'react-router-dom';
import Nav from './Nav/Nav';
import Homepage from './components/homepage/homepage';
import Login from './components/login/login';
import Register from './components/register/register';
import Hospitals from './components/hospitals/Hospitals';
import Doctors from './components/doctors/Doctors';
import './App.css';
import { useLocation } from 'react-router-dom';


function App() {
  // State to manage user authentication
  const [user, setLoginUser] = useState({});
  const [loading, setLoading] = useState(true);
 <Router>const location = useLocation();</Router> 

  // Effect hook to check authentication status on component mount
  useEffect(() => {
    const token = localStorage.getItem('token');
  
    const updateUser = async () => {
      if (token) {
        axios.defaults.headers.common['Authorization'] = `Bearer ${token}`;
  
        // Check if the current route is a protected route
        const isProtectedRoute = location.pathname === '/' || location.pathname === '/hospital' || location.pathname === '/doctors';
  
        // If it's a protected route, set the isAuthenticated state
        if (isProtectedRoute) {
          setIsAuthenticated(true);
        } else {
          // If not a protected route, set isAuthenticated to false
          setIsAuthenticated(false);
        }
      } else {
        // No token found, set isAuthenticated to false
        setIsAuthenticated(false);
      }
    };
  
    updateUser();
  
    // Set loading to false after handling authentication logic
    setLoading(false);
  }, [location.pathname, setIsAuthenticated]);
  
  
  
  
   // Include location.pathname in dependency array to update on route change

  if (loading) {
    return <p>Loading...</p>;
  } // Empty dependency array ensures the effect runs only once on mount

  // Render the main App component
  return (
    <div className="App">
      <Router>
        {/* Navigation component */}
        <Nav user={user} setLoginUser={setLoginUser} />

        {/* Define routes for different components */}
        <Routes>
          {/* Homepage route */}
          <Route
           
          
            path="/"
            element={
              <Homepage user={user} setLoginUser={setLoginUser} />
            }
          />

          {/* Login route */}
          <Route path="/login" element={<Login setLoginUser={setLoginUser} />} />

          {/* Register route */}
          <Route path="/register" element={<Register />} />

          {/* Hospitals route - Protected route, redirect to login if not authenticated */}
          <Route path="/hospital" element={user && user._id ? (<Hospitals/>) : (<Navigate to="/login" />)} />

          {/* Doctors route - Protected route, redirect to login if not authenticated */}
          <Route path="/doctors" element={user && user._id ? (<Doctors/>) : (<Navigate to="/login" />)} />
        </Routes>
      </Router>
    </div>
  );
}

// Export the App component
export default App;
