// DoctorChat.js
import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import io from 'socket.io-client';
import './DoctorChat.css';

const DoctorChat = ({ user }) => {
  const [message, setMessage] = useState('');
  const [messages, setMessages] = useState([]);
  const { doctorId } = useParams(); // Correct parameter destructuring

  useEffect(() => {
    const socket = io('http://localhost:9002/doctor-chat'); 
    console.log('Attempting to connect to WebSocket server...');
    socket.emit('join', { userId: user._id, doctorId }); 

    socket.on('message', message => {
        console.log('Received message:', message);
      setMessages(prevMessages => [...prevMessages, message]);
    });

    return () => {
        console.log('Cleaning up WebSocket connection...');
      socket.disconnect(); // Disconnect when component unmounts
    };
  }, [user._id, doctorId]);

  const handleMessageSend = () => {
    if (message.trim()) {
      const socket = io('http://localhost:9002/doctor-chat/${userId}'); // Connect to server
      socket.emit('sendMessage', { userId: user._id, doctorId, message }); // Swap userId and doctorId
      setMessage('');
    }
  };

  return (
    <div className="doctor-chat-container">
      <h2>Chat with User</h2> {/* Update heading to indicate user chat */}
      <div className="doctor-chat-messages">
        {messages.map((msg, index) => (
          <div className="doctor-chat-message" key={index}>
            <strong>{msg.user}</strong>: {msg.text}
          </div>
        ))}
      </div>
      <input
        className="doctor-chat-input"
        type="text"
        value={message}
        onChange={e => setMessage(e.target.value)}
      />
      <button className="doctor-chat-send-button" onClick={handleMessageSend}>
        Send
      </button>
    </div>
  );
};

export default DoctorChat;
