import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const Appointments = ({ user }) => {
    const [appointments, setAppointments] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
        const fetchAppointments = async () => {
            try {
                const token = localStorage.getItem('token');
                const response = await axios.get(`http://localhost:9002/appointments/${user._id}`, {
                    headers: {
                        Authorization: `Bearer ${token}`
                    }
                });
                const filteredAppointments = response.data.filter(appointment => appointment.status !== 'Cancelled');
                setAppointments(filteredAppointments);
            } catch (error) {
                console.error('Error fetching appointments:', error);
            }
        };

        fetchAppointments();
    }, [user._id]);

    const handlePayment = async (doctorId, appointmentId) => {
        try {
            const token = localStorage.getItem('token');
            const response = await axios.get(`http://localhost:9002/doctors/${doctorId}`, {
                headers: {
                    Authorization: `Bearer ${token}`
                }
            });

            const razorpayLink = response.data.razorpayLink;
            if (razorpayLink) {
                // Open the Razorpay payment link in a new tab
                window.open(razorpayLink, '_blank');
            } else {
                console.error('Razorpay link not found for the doctor');
                alert('Payment link is not available for this doctor.');
                return;
            }
        } catch (error) {
            console.error('Error fetching doctor details:', error);
            alert('An error occurred while fetching doctor details.');
        }
    };

    const handleChat = (doctorId) => {
        console.log('Chat button clicked');
        navigate(`/chat/${doctorId}/${user._id}`);
    };

    // Function to handle payment callback from Razorpay

    const handleRazorpayCallback = async (data) => {
        try {
            const { appointmentId, paymentId, status } = data;
    
            // Validate appointmentId
            const objectIdPattern = /^[0-9a-fA-F]{24}$/;
            if (!objectIdPattern.test(appointmentId)) {
                console.error('Invalid appointmentId:', appointmentId);
                return;
            }
    
            // Update appointment status based on payment status
            if (status === 'success') {
                // Make a PATCH request to update appointment status to 'Paid'
                const token = localStorage.getItem('token');
                const response = await axios.patch(
                    `http://localhost:9002/appointments/${appointmentId}`,
                    { status: 'Paid' },
                    {
                        headers: {
                            Authorization: `Bearer ${token}`
                        }
                    }
                );
    
                // Check if the appointment status was successfully updated
                if (response.status === 200) {
                    console.log('Appointment payment successful');
                    // Optionally, update the local state of appointments
                    // with the updated appointment status
                } else {
                    console.error('Failed to update appointment status');
                }
            } else {
                console.error('Appointment payment failed');
            }
        } catch (error) {
            console.error('Error processing Razorpay callback:', error);
            alert('An error occurred while processing the payment.');
        }
    };

    // Assuming we receive the payment callback from Razorpay here
    useEffect(() => {
        const data = {
            appointmentId: `${appointmentId}`, // Placeholder for appointmentId received from Razorpay
            paymentId: 'dummyPaymentId', // Placeholder for paymentId received from Razorpay
            status: 'success' // Placeholder for status received from Razorpay
        };
        handleRazorpayCallback(data); // Call the function to handle the payment callback
    }, []); // This useEffect hook will run only once when the component mounts

    return (
        <div>
            <h2>Your Appointments</h2>
            <ul>
                {appointments.map(appointment => (
                    <li key={appointment._id}>
                        Doctor: {appointment.doctorId.name}, Date: {appointment.date}, Time: {appointment.timeSlot}, Status: {appointment.status}
                        {appointment.status === "Approved" && (
                            <div>
                                <button onClick={() => handlePayment(appointment.doctorId._id, appointment._id)}>Proceed to Payment</button>
                            </div>
                        )}
                        {appointment.status === "Paid" && (
                            <button onClick={() => handleChat(appointment.doctorId._id)}>Start Chat</button>
                        )}
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default Appointments;
