// Booking.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

const Booking = ({ user }) => {
  const { doctorId } = useParams();
  const [selectedDate, setSelectedDate] = useState('');
  const [selectedTimeSlot, setSelectedTimeSlot] = useState('');
  const [bookingStatus, setBookingStatus] = useState('');
  const [appointmentId, setAppointmentId] = useState(null);

  useEffect(() => {
    const checkAppointmentStatus = async () => {
      try {
        const token = localStorage.getItem('token');
        const response = await axios.get(`http://localhost:9002/check-appointment/${doctorId}`, {
          headers: {
            Authorization: `Bearer ${token}`
          }
        });
        if (response.data.hasAppointment) {
          setBookingStatus('Already Booked');
          setAppointmentId(response.data.appointmentId);
        }
      } catch (error) {
        console.error('Error checking appointment status:', error);
      }
    };
    checkAppointmentStatus();
  }, [doctorId]);

  const handleBooking = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.post(`http://localhost:9002/book-appointment/${doctorId}`, {
        selectedDate,
        selectedTimeSlot,
      }, {
        headers: {
          Authorization: `Bearer ${token}`
        }
      });
      setBookingStatus(response.data.message);
    } catch (error) {
      console.error('Error booking appointment:', error);
      setBookingStatus('Error booking appointment');
    }
  };

  const handleCancel = async () => {
    try {
      const token = localStorage.getItem('token');
      await axios.delete(`http://localhost:9002/cancel-appointment/${appointmentId}`, {
        headers: {
          Authorization: `Bearer ${token}`
        }
      });
      setBookingStatus('Appointment Cancelled');
      setAppointmentId(null);
    } catch (error) {
      console.error('Error cancelling appointment:', error);
      setBookingStatus('Error cancelling appointment');
    }
  };

  return (
    <div>
      <h2>Book Appointment</h2>
      <label>Select Date:</label>
      <input type="date" value={selectedDate} onChange={(e) => setSelectedDate(e.target.value)} />
      <label>Select Time Slot:</label>
      <select value={selectedTimeSlot} onChange={(e) => setSelectedTimeSlot(e.target.value)}>
        <option value="9:00 AM">9:00 AM</option>
        <option value="10:00 AM">10:00 AM</option>
        {/* Add more time slots as needed */}
      </select>
      <button onClick={bookingStatus === 'Already Booked' ? handleCancel : handleBooking}>
        {bookingStatus === 'Already Booked' ? 'Cancel' : 'Book'}
      </button>
      {bookingStatus && <p>{bookingStatus}</p>}
    </div>
  );
};

export default Booking;
