// Homepage.js
import React from 'react';
import './homepage.css';
import { getUserFromLocalStorage, removeUserFromLocalStorage, setUserInLocalStorage } from './auth';

const Homepage = ({ user, setLoginUser }) => {
  const handleLogin = () => {
    const dummyUser = { _id: 'dummyUserId', username: 'dummyUser' };
    setUserInLocalStorage(dummyUser);
    setLoginUser(dummyUser);
  };

  const handleLogout = () => {
    removeUserFromLocalStorage();
    setLoginUser(null);
  };

  // Check local storage for user on component render
  const storedUser = getUserFromLocalStorage();

  return (
    <div className="homepage">
      <div className='home'>
        <p>
          Lorem ipsum, dolor sit amet consectetur adipisicing elit. Excepturi repellat error deleniti ab? Nesciunt incidunt nostrum, qui voluptatibus sunt iusto.
        </p>
      </div>
      
      {user || storedUser ? (
        // Show logout button if user is logged in
        <div className="button" onClick={handleLogout}>
          Logout
        </div>
      ) : (
        // Show login button if user is not logged in
        <div className="button" onClick={handleLogin}>
          Login
        </div>
      )}
    </div>
  );
};

export default Homepage;
