// Homepage.js
import React, { useEffect } from 'react';
import './homepage.css';

const Homepage = ({ user, setLoginUser }) => {
  // Check local storage on component mount
  useEffect(() => {
    const storedUser = localStorage.getItem('user');
    if (storedUser) {
      setLoginUser(JSON.parse(storedUser));
    }
  }, [setLoginUser]);

  // Function to handle logout
  const handleLogout = () => {
    // Clear user from local storage on logout
    localStorage.removeItem('user');
    setLoginUser(null);
  };

  // Function to handle login
  const handleLogin = () => {
    // Simulate login logic and set user in local storage
    const dummyUser = { _id: 'dummyUserId', username: 'dummyUser' };
    localStorage.setItem('user', JSON.stringify(dummyUser));
    setLoginUser(dummyUser);
  };

  return (
    <div className="homepage">
      <div className='home'>
        <p>
          Lorem ipsum, dolor sit amet consectetur adipisicing elit. Excepturi repellat error deleniti ab? Nesciunt incidunt nostrum, qui voluptatibus sunt iusto.
        </p>
      </div>
      {user && user._id ? (
        <div className="button" onClick={handleLogout}>
          Logout
        </div>
      ) : (
        <div className="button" onClick={handleLogin}>
          Login
        </div>
      )}
    </div>
  );
}

export default Homepage;
