import React, { useState,useEffect } from 'react';
import './homepage.css';
import doctorImage from '../../Images/doctor-img01.png';
import doctorImage2 from '../../Images/doctor-img02.png';
import doctorImage3 from '../../Images/doctor-img03.jpg';

const Homepage = () => {
  
  const doctors = [
    { id: 1, name: 'Dr. John Doe', specialization: 'Orthopedics', image: doctorImage },
    { id: 2, name: 'Dr. Jane Smith', specialization: 'Cardiology', image: doctorImage2 },
    { id: 2, name: 'Dr. Jane Smith', specialization: 'Cardiology', image: doctorImage3 },
   
  ];
  const [currentDoctorIndex, setCurrentDoctorIndex] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentDoctorIndex((prevIndex) => (prevIndex + 1) % doctors.length);
    }, 5000);

    return () => clearInterval(interval);
  }, [doctors.length]);

  return (
    <div className='home-container'>
    <div className="homepage">
      <div className="header-section">
        <h1>We help patients live a healthy longer life</h1>
        <p className="subtext">Providing compassionate care for all your medical needs</p>
      </div>

      <div className="doctors-section">
        <h2>Our Best Doctors</h2>
        <div className="doctor-container">
          <div className="doctor-card" style={{ backgroundImage: `url(${doctors[currentDoctorIndex].image})` }}>
            <div className="doctor-info">
              <h3>{doctors[currentDoctorIndex].name}</h3>
              <p>{doctors[currentDoctorIndex].specialization}</p>
            </div>
          </div>
        </div>
      </div>

      <div className="specialization-section">
        <h2>Specializations</h2>
        <div className="specialization-list">
          <div className="specialization-box" onClick={() => handleSpecializationClick('Orthopedics')}>
            <h3>Orthopedics</h3>
            <p>Specializing in musculoskeletal system treatments</p>
          </div>
          
        </div>
      </div>
      </div>
      <div className="footer">
        <p>&copy; 2024 Your Online Doctor Appointment Platform. All rights reserved.</p>
      </div>
    
    </div>
  );
}

export default Homepage;
