// App.js
// Import necessary libraries and components
// Import statements
import React, { useState, useEffect } from 'react';
import { Routes, Route, Navigate, BrowserRouter as Router, useLocation } from 'react-router-dom';
import axios from 'axios';
import Nav from './Nav/Nav';
import Homepage from './components/homepage/homepage';
import Login from './components/login/login';
import Register from './components/register/register';
import Hospitals from './components/hospitals/Hospitals';
import Doctors from './components/doctors/Doctors';
import { getUserFromLocalStorage, removeUserFromLocalStorage, setUserInLocalStorage } from './components/homepage/auth';
import './App.css';

function App() {
  const [user, setLoginUser] = useState({});
  const [loading, setLoading] = useState(true);
  const location = useLocation();

  useEffect(() => {
    const token = localStorage.getItem('token');

    if (token) {
      axios.defaults.headers.common['Authorization'] = `Bearer ${token}`;
      const storedUser = getUserFromLocalStorage();

      if (storedUser) {
        setLoginUser(storedUser);
      } else {
        setLoginUser(null);
      }
    } else {
      setLoginUser(null);
    }

    setLoading(false);
  }, [location.pathname]);

  if (loading) {
    return <p>Loading...</p>;
  }

  return (
    <div className="App">
      <Router>
        {/* Navigation component */}
        <Nav user={user} setLoginUser={setLoginUser} />

        {/* Routes */}
        <Routes>
          {/* Homepage route */}
          <Route
            path="/"
            element={<Homepage user={user} setLoginUser={setLoginUser} />}
          />

          {/* Login route */}
          <Route path="/login" element={<Login setLoginUser={setLoginUser} />} />

          {/* Register route */}
          <Route path="/register" element={<Register />} />

          {/* Hospitals route - Protected route, redirect to login if not authenticated */}
          <Route
            path="/hospital"
            element={user && user._id ? <Hospitals /> : <Navigate to="/login" />}
          />

          {/* Doctors route - Protected route, redirect to login if not authenticated */}
          <Route
            path="/doctors"
            element={user && user._id ? <Doctors /> : <Navigate to="/login" />}
          />
        </Routes>
      </Router>
    </div>
  );
}

export default App;
