// Nav.js
// Nav.js
import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './Nav.css';

const Nav = ({ user, setLoginUser }) => {
  const [isOpen, setIsOpen] = useState(false);
  const [initialCheckDone, setInitialCheckDone] = useState(false);

  useEffect(() => {
    if (!initialCheckDone) {
      // Perform initial authentication check
      const token = localStorage.getItem('token');
      if (token) {
        // Replace the following line with your server-side authentication endpoint
        axios.get('http://localhost:9002/verify')
          .then(response => {
            setLoginUser(response.data.user);
          })
          .catch(error => {
            console.error('Authentication error:', error);
            setLoginUser(null);
          })
          .finally(() => setInitialCheckDone(true));
      } else {
        setLoginUser(null);
        setInitialCheckDone(true);
      }
    }
  }, [initialCheckDone, setInitialCheckDone, setLoginUser]);

  return (
    <div className="Nav">
      <span className='nav-logo'>Logo</span>

      <div className={`nav-items ${isOpen && "open"}`}>
        <Link to="/">Home</Link>
        <Link to="/hospital">Hospital</Link>
        <Link to="/doctors">Doctors</Link>
        <Link to="/patientsstories">Patients Stories</Link>
        <Link to="/contacts">Contacts</Link>

        {user && user._id ? (
          <div className="logout-button" onClick={() => setLoginUser(null)}>
            Logout
          </div>
        ) : (
          <Link className='button' to="/login">Login</Link>
        )}
      </div>

      <div
        className={`nav-toggle ${isOpen && "open"}`}
        onClick={() => setIsOpen(!isOpen)}
      >
        <div className="bar"></div>
      </div>
    </div>
  );
};

export default Nav;
