import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import io from 'socket.io-client';
import './UserChat.css';

const UserChat = ({ user }) => {
  const [message, setMessage] = useState('');
  const [messages, setMessages] = useState([]);
  const { userId, doctorId } = useParams(); // Extract userId and doctorId from URL params

  useEffect(() => {
    const socket = io('http://localhost:9002/user-chat');
  
    socket.emit('join', { userId: user._id, doctorId }); // Use doctorId from URL params
  
    socket.on('message', message => {
      setMessages(prevMessages => [...prevMessages, message]);
    });
  
    return () => {
      socket.disconnect();
    };
  }, [userId, user._id, doctorId]); // Include doctorId in the dependency array
  
  const handleMessageSend = () => {
    if (message.trim()) {
      const socket = io('http://localhost:9002/user-chat');
      socket.emit('sendMessage', { userId, doctorId, message });
      setMessage('');
    }
  };

  return (
    <div className="user-chat-container">
      <h2>Chat with Doctor</h2>
      <div className="user-chat-messages">
        {messages.map((msg, index) => (
          <div className="user-chat-message" key={index}>
            <strong>{msg.user}</strong>: {msg.text}
          </div>
        ))}
      </div>
      <input
        className="user-chat-input"
        type="text"
        value={message}
        onChange={e => setMessage(e.target.value)}
      />
      <button className="user-chat-send-button" onClick={handleMessageSend}>
        Send
      </button>
    </div>
  );
};

export default UserChat;
