// Nav.js
// Nav.js
import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './Nav.css';

const Nav = ({ user, setLoginUser, pathname }) => {
  const isActive = (link) => link === pathname;

  return (
    <div className="Nav">
      <span className="nav-logo">Logo</span>

      <div className="nav-items">
        <Link to="/" className={isActive('/') ? 'active' : ''}>
          Home
        </Link>
        <Link to="/hospital" className={isActive('/hospital') ? 'active' : ''}>
          Hospital
        </Link>
        <Link to="/doctors" className={isActive('/doctors') ? 'active' : ''}>
          Doctors
        </Link>
        <Link to="/patientsstories" className={isActive('/patientsstories') ? 'active' : ''}>
          Patients Stories
        </Link>
        <Link to="/contacts" className={isActive('/contacts') ? 'active' : ''}>
          Contacts
        </Link>

        {user && user._id ? (
          <div className="logout-button" onClick={() => setLoginUser({})}>
            Logout
          </div>
        ) : (
          <Link to="/login" className={isActive('/login') ? 'active' : ''}>
            Login
          </Link>
        )}
      </div>
    </div>
  );
};

export default Nav;
