import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const Appointments = ({ user }) => {
  const [appointments, setAppointments] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchAppointments = async () => {
      try {
        const token = localStorage.getItem('token');
        const response = await axios.get(`http://localhost:9002/appointments/${user._id}`, {
          headers: {
            Authorization: `Bearer ${token}`
          }
        });
        const filteredAppointments = response.data.filter(appointment => appointment.status !== 'Cancelled');
        setAppointments(filteredAppointments);
      } catch (error) {
        console.error('Error fetching appointments:', error);
      }
    };

    fetchAppointments();
  }, [user._id]);

  return (
    <div>
      <h2>Your Appointments</h2>
      <ul>
        {appointments.map(appointment => (
          <li key={appointment._id}>
            Doctor: {appointment.doctorId.name}, Date: {appointment.date}, Time: {appointment.timeSlot}, Status: {appointment.status}
            {appointment.status === "Approved" && (
              <div dangerouslySetInnerHTML={{ __html: `<div class="razorpay-embed-btn" data-url="https://pages.razorpay.com/pl_NlkWE4J9ubwD0d/view" data-text="Pay Now" data-color="#528FF0" data-size="medium"></div><script>(function(){var d=document; var x=!d.getElementById('razorpay-embed-btn-js');if(x){ var s=d.createElement('script'); s.defer=!0;s.id='razorpay-embed-btn-js';s.src='https://cdn.razorpay.com/static/embed_btn/bundle.js';d.body.appendChild(s);} else{var rzp=window['__rzp__'];rzp && rzp.init && rzp.init();}})();</script>` }} />
            )}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Appointments;
