// App.js
import React, { useState, useEffect } from 'react';
import { Routes, Route, Navigate, BrowserRouter as Router } from 'react-router-dom';
import axios from 'axios';

import Nav from './Nav/Nav';
import Homepage from './components/homepage/homepage';
import Login from './components/login/login';
import Register from './components/register/register';
import Hospitals from './components/hospitals/Hospitals'; 
import Doctors from './components/doctors/Doctors';
import './App.css';

function App() {
  const [user, setLoginUser] = useState(null);

  // Check for existing token on page load
  useEffect(() => {
    const token = localStorage.getItem('token');

    if (token) {
      // Send a request to the server to verify the token
      axios.post('http://localhost:9002/check-auth')
        .then(response => {
          if (response.data.isAuthenticated) {
            setLoginUser({ _id: response.data.user._id, email: response.data.user.email });
          } else {
            // Token is invalid or expired, remove it from local storage
            localStorage.removeItem('token');
          }
        })
        .catch(error => {
          console.error('Error checking authentication:', error);
        });
    }
  }, []); // Empty dependency array to run only once on mount

  const handleLogin = (userData) => {
    setLoginUser(userData);
  };

  const handleLogout = () => {
    // Remove the token from local storage and reset the user state
    localStorage.removeItem('token');
    setLoginUser(null);
  };

  return (
    <div className="App">
      <Router>
        <Nav user={user} handleLogout={handleLogout} />
        <Routes>
          <Route
            exact
            path="/"
            element={
              user ? (
                <Homepage setLoginUser={setLoginUser} />
              ) : (
                <Login setLoginUser={setLoginUser} />
              )
            }
          />
          <Route path="/login" element={<Login setLoginUser={setLoginUser} />} />
          <Route path="/register" element={<Register />} />
          <Route path="/hospital" element={user ? (<Hospitals />) : (<Navigate to="/login" />)} />
          <Route path="/doctors" element={user ? (<Doctors />) : (<Navigate to="/login" />)} />
        </Routes>
      </Router>
    </div>
  );
}

export default App;
