import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Routes, Route, Navigate, useLocation } from 'react-router-dom';
import Nav from './Nav/Nav';
import Homepage from './components/homepage/homepage';
import Login from './components/login/login';
import Register from './components/register/register';
import DocRegister from './components/register/docRegister';
import Hospitals from './components/hospitals/Hospitals';
import DoctorDashboard from './components/doctors/DoctorDashboard';
import DoctorList from './components/doctors/DoctorList';
import Booking from './components/booking';
import './App.css';

function App() {
  const [user, setLoginUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const location = useLocation();

  useEffect(() => {
    // Retrieve user information from localStorage
    const storedUser = localStorage.getItem('user');
    if (storedUser) {
      setLoginUser(JSON.parse(storedUser));
    }

    // Set loading to false after retrieving user information
    setLoading(false);
  }, []);

  useEffect(() => {
    const token = localStorage.getItem('token');

    if (token) {
      axios.defaults.headers.common['Authorization'] = `Bearer ${token}`;

      // Check if the current route is a protected route
      const isProtectedRoute = location.pathname === '/' || location.pathname === '/hospital' || location.pathname === '/doctors' || location.pathname === '/doctor-dashboard' || location.pathname.startsWith('/book-appointment');

      // If it's a protected route, set the dummy user
      if (isProtectedRoute) {
        setLoginUser({ ...user }); // Update user state
      } else {
        // If not a protected route, set user to null
        setLoginUser(null);
      }
    } else {
      // No token found, set user to null
      setLoginUser(null);
    }
  }, [location.pathname]); 

  const handleLogout = () => {
    // Clear the token and remove user information from localStorage
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    setLoginUser(null);
  };

  if (loading) {
    return <p>Loading...</p>;
  }

  return (
    <div className="App">
      <Nav user={user} handleLogout={handleLogout} />
      <Routes>
        <Route path="/" element={<Homepage user={user} />} />
        <Route path="/login" element={<Login setLoginUser={setLoginUser} />} />
        <Route path="/register" element={<Register />} />
        <Route path="/register/doctor" element={<DocRegister />} />
        <Route path="/hospital" element={<Hospitals />} />
        <Route path="/doctors" element={<DoctorList />} />
        <Route path="/doctor-dashboard" element={user && user._id && user.isDoctor ? <DoctorDashboard /> : <Navigate to="/login" />} />
        <Route path="/book-appointment/:doctorId" element={<Booking user={user} />} />
      </Routes>
    </div>
  );
}

export default App;
