import React, { useState, useEffect } from 'react';
import { CardElement, useStripe, useElements } from '@stripe/react-stripe-js';
import axios from 'axios';

const CheckoutForm = ({ appointmentId, amount }) => {
  const [error, setError] = useState(null);
  const stripe = useStripe();
  const elements = useElements();

  const handleSubmit = async (event) => {
    event.preventDefault();

    if (!stripe || !elements) {
      // Stripe.js has not yet loaded.
      // Make sure to disable form submission until Stripe.js has loaded.
      return;
    }

    const result = await stripe.confirmCardPayment(clientSecret, {
      payment_method: {
        card: elements.getElement(CardElement),
        billing_details: {
          name: 'Jenny Rosen',
        },
      },
    });

    if (result.error) {
      // Show error to your customer
      setError(result.error.message);
    } else {
      if (result.paymentIntent.status === 'succeeded') {
        // Payment successful, update appointment status or navigate to success page
        alert('Payment successful!');
      }
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <CardElement />
      <button type="submit" disabled={!stripe}>
        Pay Now
      </button>
      {error && <div>{error}</div>}
    </form>
  );
};

const Payment = ({ appointmentId, amount }) => {
  const [clientSecret, setClientSecret] = useState(null);

  useEffect(() => {
    // Fetch the client secret from the server
    axios.post('/appointments/:appointmentId/create-payment-intent', { amount })
      .then(response => {
        setClientSecret(response.data.clientSecret);
      })
      .catch(error => {
        console.error('Error fetching client secret:', error);
      });
  }, [amount]);

  return (
    <div>
      <h2>Payment</h2>
      {clientSecret && <CheckoutForm appointmentId={appointmentId} amount={amount} clientSecret={clientSecret} />}
    </div>
  );
};

export default Payment;
