// Chat.js

import React, { useState, useEffect, useRef } from 'react';
import { useParams } from 'react-router-dom';
import socketIOClient from 'socket.io-client';

const Chat = () => {
  const { doctorId } = useParams();
  const [message, setMessage] = useState('');
  const [messages, setMessages] = useState([]);
  const socketRef = useRef();

  useEffect(() => {
    // Connect to the WebSocket server
    socketRef.current = socketIOClient('http://localhost:9002');

    // Listen for messages from the server
    socketRef.current.on(`doctor-${doctorId}`, (message) => {
      setMessages((prevMessages) => [...prevMessages, message]);
    });

    return () => {
      // Disconnect from the WebSocket server when component unmounts
      socketRef.current.disconnect();
    };
  }, [doctorId]);

  const sendMessage = () => {
    // Send message to the server
    socketRef.current.emit('sendMessage', { doctorId, message });
    setMessage('');
  };

  return (
    <div>
      <h1>Chat with Doctor</h1>
      <div>
        {messages.map((msg, index) => (
          <div key={index}>{msg}</div>
        ))}
      </div>
      <input type="text" value={message} onChange={(e) => setMessage(e.target.value)} />
      <button onClick={sendMessage}>Send</button>
    </div>
  );
};

export default Chat;
