// App.js
// Import necessary libraries and components
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Routes, Route, Navigate, BrowserRouter as Router } from 'react-router-dom';
import Nav from './Nav/Nav';
import Homepage from './components/homepage/homepage';
import Login from './components/login/login';
import Register from './components/register/register';
import Hospitals from './components/hospitals/Hospitals';
import Doctors from './components/doctors/Doctors';
import './App.css';

// Define the main App component
function App() {
  // State to manage user authentication
  const [user, setLoginUser] = useState({});
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  // Effect hook to check authentication status on component mount
  useEffect(() => {
    // Retrieve the authentication token from localStorage
    const token = localStorage.getItem('token');

    // If token exists, set it in axios headers
    if (token) {
      axios.defaults.headers.common['Authorization'] = `Bearer ${token}`;
      
      // Set a dummy user object as you don't have a specific user-details endpoint
      setLoginUser({ _id: user._ });
    }
  }, []); // Empty dependency array ensures the effect runs only once on mount

  // Render the main App component
  return (
    <div className="App">
      <Router>
        {/* Navigation component */}
        <Nav />

        {/* Define routes for different components */}
        <Routes>
          {/* Homepage route */}
          <Route
            exact
            path="/"
            element={
              // Render Homepage if user is authenticated, otherwise render Login
              user && user._id ? (
                <Homepage setLoginUser={setLoginUser} />
              ) : (
                <Login setLoginUser={setLoginUser} />
              )
            }
          />

          {/* Login route */}
          <Route path="/login" element={<Login setLoginUser={setLoginUser} />} />

          {/* Register route */}
          <Route path="/register" element={<Register />} />

          {/* Hospitals route - Protected route, redirect to login if not authenticated */}
          <Route
            path="/hospital"
            element={
              loading ? (
                <div>Loading...</div>
              ) : user && user._id ? (
                <Hospitals />
              ) : (
                <Navigate to="/login" />
              )
            }
          />

          {/* Doctors route - Protected route, redirect to login if not authenticated */}
          <Route path="/doctors" element={user && user._id ? (<Doctors/>) : (<Navigate to="/login" />)} />
        </Routes>
      </Router>
    </div>
  );
}

// Export the App component
export default App;
