import React, { useState } from 'react';
import { Elements } from '@stripe/react-stripe-js';
import { loadStripe } from '@stripe/stripe-js';
import CheckoutForm from './CheckoutForm';
import axios from 'axios';

const stripePromise = loadStripe('pk_test_51OsfLSSDK53yDPuaMdZw2Vy4TyJGB8Nug8Yc4nvATKi7iOqIvLRli0SIdmkdsQBERIwMO1Ekb6GdW5TYO4pjz72W00Luof9MUU');

const Payment = () => {
  const [amount, setAmount] = useState(''); // Initialize amount state
  const [clientSecret, setClientSecret] = useState('');

  const handleAmountChange = (e) => {
    setAmount(e.target.value); 
  };

  const handlePayment = async () => {
    try {
      // Parse the amount to a number
      const numericAmount = parseFloat(amount);
      const response = await axios.post('/payment', { amount: numericAmount });
      setClientSecret(response.data.clientSecret);
      console.log(response);
    } catch (error) {
      console.error('Error fetching client secret:', error);
    }
  };
  

  return (
    <div>
      <h2>Payment</h2>
      <div>
        <label>
          Enter Amount:
          <input type="number" value={amount} onChange={handleAmountChange} />
        </label>
      </div>
      {amount > 0 && ( // Render CheckoutForm only if amount is greater than 0
        <Elements stripe={stripePromise}>
          <CheckoutForm clientSecret={clientSecret} amount={amount}/>
        </Elements>
      )}
      
    </div>
  );
};

export default Payment;
