// src/components/AppointmentForm/AppointmentForm.js
import React, { useState } from 'react';
import api from '../../services/api.js'; // Import the API service if you need to make requests to your backend

const AppointmentForm = () => {
    // State to manage form input fields
    const [formData, setFormData] = useState({
        name: '',
        email: '',
        doctor: '',
        date: '',
        time: '',
    });

    // Function to handle form submission
    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            // Make a request to your backend API to submit the appointment data
            const response = await api.post('/api/appointments', formData);
            // Handle success response (e.g., show a success message to the user)
            console.log(response.data);
        } catch (error) {
            // Handle error (e.g., display an error message to the user)
            console.error('Error submitting appointment:', error);
        }
    };

    // Function to handle input changes
    const handleChange = (e) => {
        setFormData({
            ...formData,
            [e.target.name]: e.target.value,
        });
    };

    return (
        <div>
            <h2>Appointment Form</h2>
            <form onSubmit={handleSubmit}>
                <div>
                    <label>Name:</label>
                    <input type="text" name="name" value={formData.name} onChange={handleChange} required />
                </div>
                <div>
                    <label>Email:</label>
                    <input type="email" name="email" value={formData.email} onChange={handleChange} required />
                </div>
                <div>
                    <label>Doctor:</label>
                    <select name="doctor" value={formData.doctor} onChange={handleChange} required>
                        <option value="">Select Doctor</option>
                        {/* Populate the options with doctors available */}
                        {/* Example: <option value="doctor1">Doctor 1</option> */}
                    </select>
                </div>
                <div>
                    <label>Date:</label>
                    <input type="date" name="date" value={formData.date} onChange={handleChange} required />
                </div>
                <div>
                    <label>Time:</label>
                    <input type="time" name="time" value={formData.time} onChange={handleChange} required />
                </div>
                <button type="submit">Submit</button>
            </form>
        </div>
    );
};

export default AppointmentForm;
