import React, { useState } from "react";
import "./register.css";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const Register = () => {
  const history = useNavigate();

  const [user, setUser] = useState({
    name: "",
    email: "",
    password: "",
    reEnterPassword: "",
    isDoctor: false,
    qualification: "",
    degree: "",
    timeslots: {
      monday: "",
      tuesday: "",
      wednesday: "",
      thursday: "",
      friday: "",
      saturday: "",
      sunday: "",
    },
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setUser({
      ...user,
      [name]: value,
    });
  };

  const register = () => {
    const {
      name,
      email,
      password,
      reEnterPassword,
      isDoctor,
      qualification,
      degree,
      timeslots,
    } = user;

    if (name && email && password && reEnterPassword) {
      if (isDoctor && qualification && degree) {
        axios
          .post("http://localhost:9002/register/doctor", {
            name,
            email,
            password,
            specialization,
            degree,
            timeslots,
          })
          .then((res) => {
            alert(res.data.message);
            history("/login");
          })
          .catch((error) => {
            console.error("Registration error:", error);
            alert("An error occurred during registration");
          });
      } else {
        axios
          .post("http://localhost:9002/register", {
            name,
            email,
            password,
          })
          .then((res) => {
            alert(res.data.message);
            history("/login");
          })
          .catch((error) => {
            console.error("Registration error:", error);
            alert("An error occurred during registration");
          });
      }
    } else {
      alert("Invalid input");
    }
  };

  return (
    <div className="register">
      <h1>Register</h1>
      <input
        type="text"
        name="name"
        value={user.name}
        placeholder="Your Name"
        onChange={handleChange}
      />
      <input
        type="text"
        name="email"
        value={user.email}
        placeholder="Your Email"
        onChange={handleChange}
      />
      <input
        type="password"
        name="password"
        value={user.password}
        placeholder="Your Password"
        onChange={handleChange}
      />
      <input
        type="password"
        name="reEnterPassword"
        value={user.reEnterPassword}
        placeholder="Re-enter Password"
        onChange={handleChange}
      />
      <label>
        Register as Doctor:
        <input
          type="checkbox"
          name="isDoctor"
          checked={user.isDoctor}
          onChange={(e) => setUser({ ...user, isDoctor: e.target.checked })}
        />
      </label>
      {user.isDoctor && (
        <>
          <input
            type="text"
            name="specialization"
            value={user.specialization}
            placeholder="Your specialization"
            onChange={handleChange}
          />
          <input
            type="text"
            name="degree"
            value={user.degree}
            placeholder="Your Degree"
            onChange={handleChange}
          />
          <div>
            <label>Timeslots:</label>
            <input
              type="text"
              name="monday"
              value={user.timeslots.monday}
              placeholder="Monday"
              onChange={(e) =>
                setUser({
                  ...user,
                  timeslots: { ...user.timeslots, monday: e.target.value },
                })
              }
            />
            {/* Repeat similar inputs for other days */}
          </div>
        </>
      )}
      <div className="button" onClick={register}>
        Register
      </div>
      <div>or</div>
      <div className="button" onClick={() => history("/login")}>
        Login
      </div>
    </div>
  );
};

export default Register;
