import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './login.css'
const Login = ({ setLoginUser }) => {
  const history = useNavigate();
  const [user, setUser] = useState({
    email: '',
    password: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setUser({
      ...user,
      [name]: value,
    });
  };

  const login = () => {
    axios.post('http://localhost:9002/api/auth/login', user)
      .then((res) => {
        alert(res.data.message);
        setLoginUser({ _id: res.data.user._id, email: res.data.user.email });
        localStorage.setItem('token', res.data.token); // Save the token in local storage
        history('/');
      })
      .catch((error) => {
        console.error('Login error:', error);
      });
  };

  return (
    <div className="login">
      <h1>Login</h1>
      <input type="text" name="email" value={user.email} onChange={handleChange} placeholder="Enter your Email"></input>
      <input type="password" name="password" value={user.password} onChange={handleChange} placeholder="Enter your Password"></input>
      <div className="button" onClick={login}>Login</div>
      <div>or</div>
      <div className="button" onClick={() => history('/register')}>Register</div>
    </div>
  );
};

export default Login;
