import React, { useState, useEffect, useRef } from 'react';
import { useParams } from 'react-router-dom';
import io from 'socket.io-client';
import './UserChat.css'; // Import CSS file

const UserChat = ({ user }) => {
    const { userId: currentUserID } = user; // Destructure the current user's ID
    const { userId: doctorId, doctorId: chatDoctorId } = useParams(); // Destructure the doctor's ID from params
    const [messages, setMessages] = useState([]);
    const [newMessage, setNewMessage] = useState('');
    const socket = io('http://localhost:9002');
    const messageInputRef = useRef(null);

    useEffect(() => {
        // Use the appropriate ID based on the current user's role
        const targetUserId = currentUserID === chatDoctorId ? doctorId : currentUserID;
        socket.emit('getMessages', { userId: targetUserId, doctorId: chatDoctorId });

        socket.on('messages', (messages) => {
            setMessages(messages);
        });

        return () => {
            socket.off('messages');
        };
    }, [chatDoctorId, currentUserID, doctorId]);

    const handleMessageSend = () => {
        if (newMessage.trim() === '') return;

        // Use the appropriate ID based on the current user's role
        const targetUserId = currentUserID === chatDoctorId ? doctorId : currentUserID;
        socket.emit('sendMessage', { userId: targetUserId, doctorId: chatDoctorId, content: newMessage });
        setNewMessage('');
        // Focus the input field after sending the message
        messageInputRef.current.focus();
    };

    return (
        <div className="user-chat-container">
            <h2>Chat</h2>
            <div className="chat-messages">
                {messages.map((message, index) => (
                    <div key={index} className={`message ${message.userId === currentUserID ? 'left' : 'right'}`}>
                        <p className="message-content">{message.content}</p>
                        <span className="message-timestamp">{message.createdAt}</span>
                    </div>
                ))}
            </div>
            <div className="input-container">
                <input
                    type="text"
                    value={newMessage}
                    onChange={(e) => setNewMessage(e.target.value)}
                    ref={messageInputRef}
                    className="message-input"
                />
                <button onClick={handleMessageSend} className="send-button">Send</button>
            </div>
        </div>
    );
};

export default UserChat;
