import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './Nav.css';

const Nav = () => {
  const [isOpen, setIsOpen] = useState(false);

  const closeNav = () => {
    setIsOpen(false);
  };

  return (
    <div className="Nav">
      <span className='nav-logo'>Logo</span>

      <div className={`nav-items ${isOpen && "open"}`}>
        <Link to="/" onClick={closeNav}>Home</Link>
        <Link to="/hospital" onClick={closeNav}>Hospital</Link>
        <Link to="/doctors" onClick={closeNav}>Doctors</Link>
        <Link to="/patientsstories" onClick={closeNav}>Patients Stories</Link>
        <Link to="/contacts" onClick={closeNav}>Contacts</Link>
      </div>

      <div
        className={`nav-toggle ${isOpen && "open"}`}
        onClick={() => setIsOpen(!isOpen)}
      >
        <div className="bar"></div>
      </div>
    </div>
  );
};

export default Nav;
