import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

const DocRegister = () => {
  const navigate = useNavigate();
  const [doctor, setDoctor] = useState({
    name: "",
    email: "",
    password: "",
    specialization: "",
    degree: "",
    photo: null, // New state for storing the uploaded photo
    timeslots: {
      monday: { start: "", end: "" },
    },
  });

  // Handle changes in input fields
  const handleChange = (e) => {
    const { name, value } = e.target;
    setDoctor((prevDoctor) => ({
      ...prevDoctor,
      [name]: value,
    }));
  };

  // Handle changes in the photo input field
  const handlePhotoChange = (e) => {
    const file = e.target.files[0];
    setDoctor((prevDoctor) => ({
      ...prevDoctor,
      photo: file,
    }));
  };

  // Handle changes in the timeslots input fields
  const handleTimeslotChange = (e, day, timeType) => {
    const { value } = e.target;
    setDoctor((prevDoctor) => ({
      ...prevDoctor,
      timeslots: {
        ...prevDoctor.timeslots,
        [day]: {
          ...prevDoctor.timeslots[day],
          [timeType]: value,
        },
      },
    }));
  };

  // Function to register a new doctor
  const registerDoctor = async () => {
    // Create a new FormData object to send data including the photo
    const formData = new FormData();
    formData.append("name", doctor.name);
    formData.append("email", doctor.email);
    formData.append("password", doctor.password);
    formData.append("specialization", doctor.specialization);
    formData.append("degree", doctor.degree);
    formData.append("photo", doctor.photo); // Append photo to FormData
    formData.append("timeslots", JSON.stringify(doctor.timeslots)); // Append timeslots as JSON string

    try {
      // Send a POST request to register the doctor
      const response = await axios.post("http://localhost:9002/register/doctor", formData, {
        headers: {
          "Content-Type": "multipart/form-data", // Set content type to multipart/form-data for file upload
        },
      });
      
      // Display a success message and navigate to the login page upon successful registration
      alert(response.data.message);
      navigate("/login");
    } catch (error) {
      // Handle registration error
      console.error("Doctor registration error:", error);
      alert("An error occurred during registration");
    }
  };

  return (
    <div className="doc-register">
      <h1>Doctor Registration</h1>
      {/* Input fields for doctor information */}
      <input type="text" name="name" value={doctor.name} placeholder="Your Name" onChange={handleChange} />
      <input type="text" name="email" value={doctor.email} placeholder="Your Email" onChange={handleChange} />
      <input type="password" name="password" value={doctor.password} placeholder="Your Password" onChange={handleChange} />
      <input type="text" name="specialization" value={doctor.specialization} placeholder="Your Specialization" onChange={handleChange} />
      <input type="text" name="degree" value={doctor.degree} placeholder="Your Degree" onChange={handleChange} />

      {/* Input field for uploading photo */}
      <input type="file" accept="image/*" onChange={handlePhotoChange} />

      {/* Input fields for timeslots */}
      <div>
        <label>Monday:</label>
        <input type="text" value={doctor.timeslots.monday.start} placeholder="Start Time" onChange={(e) => handleTimeslotChange(e, "monday", "start")} />
        <input type="text" value={doctor.timeslots.monday.end} placeholder="End Time" onChange={(e) => handleTimeslotChange(e, "monday", "end")} />
      </div>

      {/* Button to register the doctor */}
      <div className="button" onClick={registerDoctor}>
        Register as Doctor
      </div>
    </div>
  );
};

export default DocRegister;
