import React, { useState } from 'react';
import { CardElement, useStripe, useElements } from '@stripe/react-stripe-js';
import { useNavigate } from 'react-router-dom';

const CheckoutForm = ({ clientSecret }) => {
  const [error, setError] = useState(null);
  const [amount, setAmount] = useState(''); // State to hold the amount entered by the user
  const stripe = useStripe();
  const elements = useElements();
  const navigate = useNavigate();
  const handlePayment = async () => {
    try {
      const response = await axios.post('/payment', { amount });
      setClientSecret(response.data.clientSecret);
    } catch (error) {
      console.error('Error fetching client secret:', error);
    }
  };
  const handleSubmit = async (event) => {
    event.preventDefault();

    if (!stripe || !elements || !amount) {
      setError('Please enter a valid amount.');
      return;
    }

    try {
      const result = await stripe.confirmCardPayment(clientSecret, {
        payment_method: {
          card: elements.getElement(CardElement),
          billing_details: {
            name: 'Tripti Bhattacharyya', 
          },
        },
        amount: parseFloat(amount) * 100, // Convert amount to a number and multiply by 100 for cents
        currency: 'inr', // Adjust currency as needed
      });

      if (result.error) {
        setError(result.error.message);
      } else {
        if (result.paymentIntent.status === 'succeeded') {
          navigate('/payment/success');
        }
      }
    } catch (error) {
      setError('An error occurred while processing the payment.');
    }
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <label>
          Enter Amount:
          <input
            type="number"
            value={amount}
            onChange={(e) => setAmount(e.target.value)}
            required
          />
        </label>
        <CardElement />
        <button type="submit" disabled={!stripe}>
          Pay Now
        </button>
      </form>
      {error && <div>{error}</div>}
    </div>
  );
};

export default CheckoutForm;
