// App.js
// App.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Routes, Route, Navigate, BrowserRouter as Router } from 'react-router-dom';
import Nav from './Nav/Nav';
import Homepage from './components/homepage/homepage';
import Login from './components/login/login';
import Register from './components/register/register';
import Hospitals from './components/hospitals/Hospitals';
import Doctors from './components/doctors/Doctors';
import './App.css';
import { useLocation } from 'react-router-dom';

function App() {
  const [user, setLoginUser] = useState({});
  const [loading, setLoading] = useState(true);
  <Router>const location = useLocation();</Router>

  useEffect(() => {
    const checkAuthStatus = async () => {
      const token = localStorage.getItem('token');

      if (token) {
        axios.defaults.headers.common['Authorization'] = `Bearer ${token}`;
        
        try {
          const response = await axios.get('http://localhost:9002/check-auth');
          setLoginUser(response.data.user);
        } catch (error) {
          console.error('Authentication check failed:', error);
          setLoginUser(null);
        }
      } else {
        setLoginUser(null);
      }

      setLoading(false);
    };

    checkAuthStatus();
  }, [location.pathname]);

  if (loading) {
    return <p>Loading...</p>;
  }

  return (
    <div className="App">
      <Router>
        <Nav user={user} setLoginUser={setLoginUser} />

        <Routes>
          <Route
            path="/"
            element={
              <Homepage user={user} setLoginUser={setLoginUser} />
            }
          />

          <Route path="/login" element={<Login setLoginUser={setLoginUser} />} />
          <Route path="/register" element={<Register />} />
          <Route path="/hospital" element={user ? (<Hospitals />) : (<Navigate to="/login" />)} />
          <Route path="/doctors" element={user ? (<Doctors />) : (<Navigate to="/login" />)} />
        </Routes>
      </Router>
    </div>
  );
}

export default App;
