import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Routes, Route, Navigate, BrowserRouter as Router, useLocation } from 'react-router-dom'; // Import useLocation hook
import Nav from './Nav/Nav';
import Homepage from './components/homepage/homepage';
import Login from './components/login/login';
import Register from './components/register/register';
import DocRegister from './components/register/docRegister';
import Hospitals from './components/hospitals/Hospitals';
import DoctorDashboard from './components/doctors/DoctorDashboard';
import DoctorList from './components/doctors/DoctorList';
import Booking from './components/booking';
import './App.css';

// Define the main App component
function App() {
  // State to manage user authentication
  const [user, setLoginUser] = useState({});
  const [loading, setLoading] = useState(true);
  const location = useLocation(); // Use useLocation hook to access location

  // Effect hook to check authentication status on component mount and location change
  useEffect(() => {
    const token = localStorage.getItem('token');

    if (token) {
      axios.defaults.headers.common['Authorization'] = `Bearer ${token}`;

      // Check if the current route is a protected route
      const isProtectedRoute = location.pathname === '/' || location.pathname === '/hospital' || location.pathname === '/doctors' || location.pathname === '/doctor-dashboard' || location.pathname.startsWith('/book-appointment');

      // If it's a protected route, set the dummy user
      if (isProtectedRoute) {
        setLoginUser({ _id: user._id, isDoctor: user.isDoctor });
      } else {
        // If not a protected route, set user to null or an empty object
        setLoginUser(null); // or setLoginUser({});
      }
    } else {
      // No token found, set user to null or an empty object
      setLoginUser(null); // or setLoginUser({});
    }

    // Set loading to false after handling authentication logic
    setLoading(false);
  }, [location.pathname]); // Include location.pathname in dependency array to update on route change
  const handleLogout = () => {
    // Clear the token and set user to null
    localStorage.removeItem('token');
    setLoginUser(null);
  };

  if (loading) {
    return <p>Loading...</p>;
  }

  // Render the main App component
  return (
    <div className="App">
      {/* Navigation component */}
      <Nav user={user} setLoginUser={setLoginUser} handleLogout={handleLogout} />
      
      {/* Define routes for different components */}
      <Routes>
        {/* Homepage route */}
        <Route
          path="/"
          element={<Homepage user={user} setLoginUser={setLoginUser} />}
        />

        {/* Login route */}
        <Route path="/login" element={<Login setLoginUser={setLoginUser} />} />
        
        {/* Register routes */}
        <Route path="/register" element={<Register />} />
        <Route path="/register/doctor" element={<DocRegister />} />

        {/* Hospitals route - Protected route, redirect to login if not authenticated */}
        <Route path="/hospital" element={user && user._id ? (<Hospitals/>) : (<Navigate to="/login" />)} />

        {/* Doctors route - Protected route, redirect to login if not authenticated */}
        <Route path="/doctors" element={user && user._id ? (<DoctorList />) : (<Navigate to="/login" />)} />
        
        {/* Doctor Dashboard route - Protected route, redirect to login if not authenticated */}
        <Route path="/doctor-dashboard" element={user && user._id && user.isDoctor ? (<DoctorDashboard/>) : (<Navigate to="/login" />)} />

        {/* Book Appointment route - Protected route, redirect to login if not authenticated */}
        <Route path="/book-appointment/:doctorId" element={user && user._id ? (<Booking user={user} />) : (<Navigate to="/login" />)} />
      </Routes>
    </div>
  );
}

// Export the App component
export default App;
