// Homepage.js
import React, { useEffect } from 'react';
import './homepage.css';
import { getUserFromLocalStorage, setUserInLocalStorage, removeUserFromLocalStorage } from './auth';  // Adjust the path accordingly

const Homepage = ({ user, setLoginUser }) => {
  // Check local storage on component mount
  useEffect(() => {
    const storedUser = getUserFromLocalStorage();
    if (storedUser) {
      setLoginUser(storedUser);
    }
  }, [setLoginUser]);

  // Function to handle logout
  const handleLogout = () => {
    removeUserFromLocalStorage();
    setLoginUser(null);
  };

  // Function to handle login
  const handleLogin = () => {
    const dummyUser = { _id: 'dummyUserId', username: 'dummyUser' };
    setUserInLocalStorage(dummyUser);
    setLoginUser(dummyUser);
  };

  return (
    <div className="homepage">
      <div className='home'>
        <p>
          Lorem ipsum, dolor sit amet consectetur adipisicing elit. Excepturi repellat error deleniti ab? Nesciunt incidunt nostrum, qui voluptatibus sunt iusto.
        </p>
      </div>
      {user && user._id ? (
        <div className="button" onClick={handleLogout}>
          Logout
        </div>
      ) : (
        <div className="button" onClick={handleLogin}>
          Login
        </div>
      )}
    </div>
  );
}

export default Homepage;
