// App.js
// Import necessary libraries and components
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Routes, Route, Navigate, BrowserRouter as Router, useLocation } from 'react-router-dom';
import Nav from './Nav/Nav';
import Homepage from './components/homepage/homepage';
import Login from './components/login/login';
import Register from './components/register/register';
import Hospitals from './components/hospitals/Hospitals';
import Doctors from './components/doctors/Doctors';
import './App.css';

function App() {
  const [loading, setLoading] = useState(true);
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  useEffect(() => {
    const token = localStorage.getItem('token');
    const location = useLocation(); // Use useLocation directly inside the useEffect

    const updateUser = async () => {
      if (token) {
        axios.defaults.headers.common['Authorization'] = `Bearer ${token}`;

        // Check if the current route is a protected route
        const isProtectedRoute = location.pathname === '/' || location.pathname === '/hospital' || location.pathname === '/doctors';

        // If it's a protected route, set the isAuthenticated state
        if (isProtectedRoute) {
          setIsAuthenticated(true);
        } else {
          // If not a protected route, set isAuthenticated to false
          setIsAuthenticated(false);
        }
      } else {
        // No token found, set isAuthenticated to false
        setIsAuthenticated(false);
      }
    };

    updateUser();

    // Set loading to false after handling authentication logic
    setLoading(false);
  }, [setIsAuthenticated]);

  if (loading) {
    return <p>Loading...</p>;
  }

  return (
    <div className="App">
      <Router>
        <Nav isAuthenticated={isAuthenticated} setIsAuthenticated={setIsAuthenticated} />

        <Routes>
          <Route
            path="/"
            element={
              isAuthenticated ? (
                <Homepage setIsAuthenticated={setIsAuthenticated} />
              ) : (
                <Navigate to="/login" />
              )
            }
          />
          <Route path="/login" element={<Login setIsAuthenticated={setIsAuthenticated} />} />
          <Route path="/register" element={<Register />} />
          <Route path="/hospital" element={isAuthenticated ? <Hospitals /> : <Navigate to="/login" />} />
          <Route path="/doctors" element={isAuthenticated ? <Doctors /> : <Navigate to="/login" />} />
        </Routes>
      </Router>
    </div>
  );
}

export default App;
