// App.js
// Import necessary libraries and components
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Routes, Route, Navigate } from 'react-router-dom';
import Nav from './Nav/Nav';
import Homepage from './components/homepage/homepage';
import Login from './components/login/login';
import Register from './components/register/register';
import AppointmentForm from './components/AppointmentForm/appointForm';
import DoctorList from './components/DoctorList/'; // Import DoctorList component
import DoctorProfile from './components/DoctorProfile/DoctorProf'; // Import DoctorProfile component
import './App.css';
import { useLocation } from 'react-router-dom';

function App() {
    const [user, setUser] = useState(null);
    const [loading, setLoading] = useState(true);
    const location = useLocation();

    useEffect(() => {
        const token = localStorage.getItem('token');
        if (token) {
            axios.defaults.headers.common['Authorization'] = `Bearer ${token}`;
            axios.get('/auth/me')
                .then(response => {
                    setUser(response.data.user);
                    setLoading(false);
                })
                .catch(error => {
                    console.error('Error fetching user:', error);
                    setLoading(false);
                });
        } else {
            setLoading(false);
        }
    }, []);

    const handleLogout = () => {
        localStorage.removeItem('token');
        setUser(null);
    };

    if (loading) {
        return <p>Loading...</p>;
    }

    // Determine if the current route is a protected route
    const isProtectedRoute = location.pathname === '/' || location.pathname === '/appointment' || location.pathname === '/doctors';

    return (
        <div className="App">
            <Nav user={user} handleLogout={handleLogout} />
            <Routes>
                <Route
                    path="/"
                    element={<Homepage user={user} />}
                />
                <Route
                    path="/login"
                    element={<Login setUser={setUser} />}
                />
                <Route
                    path="/register"
                    element={<Register />}
                />
                <Route
                    path="/appointment"
                    element={user ? <AppointmentForm /> : <Navigate to="/login" />}
                />
                <Route
                    path="/doctors"
                    element={<DoctorList />}
                />
                <Route
                    path="/doctor/:id"
                    element={<DoctorProfile />}
                />
              
            </Routes>
        </div>
    );
}

export default App;
