// Register.js

// Register.js
import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./register.css";
import axios from "axios";
const Register = () => {
  const navigate = useNavigate();
  
  // State to manage redirection to doctor registration
  const [redirectToDoctorRegistration, setRedirectToDoctorRegistration] = useState(false);
  
  // State to manage user registration data
  const [user, setUser] = useState({
    name: "",
    email: "",
    password: "",
    reEnterPassword: "",
    isDoctor: false,
  });

  // Function to handle input change
  const handleChange = (e) => {
    const { name, value } = e.target;
  
    // Check if the changed field is part of the timeslots object
    if (name.startsWith("timeslots")) {
      const [_, day, timeType] = name.split(".");
      setDoctor({
        ...doctor,
        timeslots: {
          ...doctor.timeslots,
          [day]: {
            ...doctor.timeslots[day],
            [timeType]: value,
          },
        },
      });
    } else {
      setDoctor({
        ...doctor,
        [name]: value,
      });
    }
  };
  
  // Function to handle registration
  const register = () => {
    const { name, email, password, reEnterPassword } = user
    if( name && email && password && (password === reEnterPassword)){
        axios.post("http://localhost:9002/register", user)
        .then( res => {
            alert(res.data.message)
            navigate("/login")
        })
    } else {
        alert("invlid input")
    }
     
  };

  // Redirect to doctor registration page if isDoctor is selected
  if (redirectToDoctorRegistration) {
    navigate("/register/doctor");
    return null; // Returning null to prevent rendering anything while redirecting
  }

  return (
    <div className="register">
      <h1>Register</h1>
      {/* Input fields for user registration */}
      <input
        type="text"
        name="name"
        value={user.name}
        placeholder="Your Name"
        onChange={handleChange}
      />
      <input
        type="text"
        name="email"
        value={user.email}
        placeholder="Your Email"
        onChange={handleChange}
      />
      <input
        type="password"
        name="password"
        value={user.password}
        placeholder="Your Password"
        onChange={handleChange}
      />
      <input
        type="password"
        name="reEnterPassword"
        value={user.reEnterPassword}
        placeholder="Re-enter Password"
        onChange={handleChange}
      />
      <label>
        Register as Doctor:
        <input
          type="checkbox"
          name="isDoctor"
          checked={user.isDoctor}
          onChange={(e) => {
            setUser({ ...user, isDoctor: e.target.checked });
            if (e.target.checked) {
              setRedirectToDoctorRegistration(true);
            }
          }}
        />
      </label>
      <div className="button" onClick={register}>
        Register
      </div>
      <div>or</div>
      <div className="button" onClick={() => navigate("/login")}>
        Login
      </div>
    </div>
  );
};

export default Register;
