import React, { useState, useEffect } from 'react';
import { Elements } from '@stripe/react-stripe-js';
import { loadStripe } from '@stripe/stripe-js';
import CheckoutForm from './CheckoutForm';
import axios from 'axios';

const stripePromise = loadStripe('pk_test_51OsfLSSDK53yDPuaMdZw2Vy4TyJGB8Nug8Yc4nvATKi7iOqIvLRli0SIdmkdsQBERIwMO1Ekb6GdW5TYO4pjz72W00Luof9MUU');

const Payment = ({ amount }) => {
  const [clientSecret, setClientSecret] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchClientSecret = async () => {
      try {
        const response = await axios.post('http://localhost:9002/payment', { amount });
        setClientSecret(response.data.clientSecret);
      } catch (error) {
        console.error('Error fetching client secret:', error);
        setError('An error occurred while fetching the client secret. Please try again later.');
      }
    };

    fetchClientSecret();
  }, [amount]);

  return (
    <div>
      <h2>Payment</h2>
      {clientSecret && (
        <Elements stripe={stripePromise}>
          <CheckoutForm clientSecret={clientSecret} amount={amount} />
        </Elements>
      )}
    </div>
  );
};

export default Payment;
