// DoctorDashboard.js

import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Stripe from 'stripe';

const stripe = Stripe('your_stripe_secret_key');

const DoctorDashboard = () => {
  const [appointments, setAppointments] = useState([]);

  const fetchAppointments = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get('http://localhost:9002/doctor-dashboard', {
        headers: {
          Authorization: `Bearer ${token}`
        }
      });
      setAppointments(response.data);
    } catch (error) {
      console.error('Error fetching appointments:', error);
    }
  };

  useEffect(() => {
    fetchAppointments();
  }, []);

  const handleApprove = async (appointmentId) => {
    try {
      const token = localStorage.getItem('token');
      await axios.put(`http://localhost:9002/appointments/${appointmentId}/approve`, null, {
        headers: {
          Authorization: `Bearer ${token}`
        }
      });
      // After approval, initiate payment processing
      await initiatePayment(appointmentId);
      // After payment processing, fetch appointments again to update the list
      fetchAppointments();
    } catch (error) {
      console.error('Error approving appointment:', error);
    }
  };

  const initiatePayment = async (appointmentId) => {
    try {
      // Get payment details for the appointment from the backend
      const paymentDetailsResponse = await axios.get(`http://localhost:9002/appointments/${appointmentId}/payment-details`);
      const paymentDetails = paymentDetailsResponse.data;

      // Call Stripe API to process the payment
      const paymentIntent = await stripe.paymentIntents.create({
        amount: paymentDetails.amount,
        currency: 'usd',
      });
      console.log('Payment intent created:', paymentIntent);
    } catch (error) {
      console.error('Error initiating payment:', error);
    }
  };

  return (
    <div>
      <h1>Doctor Dashboard</h1>
      <h2>Appointments</h2>
      <ul>
        {appointments.map(appointment => (
          <li key={appointment._id}>
            <div>User: {appointment.userId.name}</div>
            <div>Date: {appointment.date}</div>
            <div>Time Slot: {appointment.timeSlot}</div>
            <div>Status: {appointment.status}</div>
            {appointment.status === 'Pending' && (
              <button onClick={() => handleApprove(appointment._id)}>Approve</button>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default DoctorDashboard;
