import React, { useState } from 'react';
import { CardElement, useStripe, useElements } from '@stripe/react-stripe-js';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const CheckoutForm = ({ clientSecret, amount }) => {
  const [error, setError] = useState(null);
  const stripe = useStripe();
  const elements = useElements();
  const navigate = useNavigate();

  const handlePayment = async () => {
    try {
      const result = await stripe.confirmCardPayment(clientSecret, {
        payment_method: {
          card: elements.getElement(CardElement),
          billing_details: {
            name: 'Tripti Bhattacharyya', 
          },
        },
        amount: parseFloat(amount) * 100, // Convert amount to a number and multiply by 100 for cents
        currency: 'inr', // Adjust currency as needed
      });

      if (result.error) {
        setError(result.error.message);
      } else {
        if (result.paymentIntent.status === 'succeeded') {
          navigate('/payment/success');
        }
      }
    } catch (error) {
      setError('An error occurred while processing the payment.');
    }
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    handlePayment(); // Call handlePayment function to initiate the payment process
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <label>
          Enter Amount:
          <input
            type="number"
            value={amount}
           
            required
          />
        </label>
        <CardElement />
        
      </form>
      {error && <div>{error}</div>}
    </div>
  );
};

export default CheckoutForm;
